import React, { Component } from 'react'
import {Link } from "react-router-dom";
import {useEffect, useState} from 'react'
import "../Login/signup.css"

export default function signup(){
      const [user,setUser] = useState({
        name:"", email:"", password:""
      })
      const [data,setData] = useState([]);
      let name ,value
      const handleInputs= (e)=>{
        console.log(e);
        name = e.target.name;
        value = e.target.value;

        setUser({...user,[name]:value})
      }

      const addData = (e) =>{
        e.preventDefault();

        const {name , email, password} = user;
        if((name==="")){
          alert("name field is required")
        }else if(email===""){
          alert("email field is required")
        }else if(!email.includes("@")){
          alert("please enter valid email addres")
        }else if(password===""){
          alert("Password field is required")
        }else if(password.length<6){
          alert("password length should be greater than 5")
        }else{
          console.log("data added successfully")

         localStorage.setItem("userkey",JSON.stringify([...data,user]));
        }
      }
        
      

    return (
        <>
        <div className="log-box">
           <h1>Welcome to Auction10</h1>
            <div className='log-container'>
                <h2>Sign Up</h2>
                <form method='POST'>
                    <h4>Enter your Name:</h4>
                    <input type='text' name='name' value={user.name} onChange={handleInputs}></input>
                    <h4>Enter your Email:</h4>
                    <input type='email' name='email' value={user.email} onChange={handleInputs}></input>
                    <h4>Create Password:</h4>
                    <input type='password' name='password' value={user.password} onChange={handleInputs}></input>

                   <Link 
                   //to='/OTP'
                   ><button onClick={addData} className='button sign-in-button'>Next</button></Link>
                </form>
            </div>
        </div>
        </>
    )
  
}
