import React from 'react'
import {Link } from "react-router-dom";

function Nextitem(values,nextinput){
    if(values.value.length=values.getAttribute('maxlength')){
        otp_input[nextinput].focus();
    }
    }
    Object.oninput= function  OTPitems(){
        if (this.value.length > this.maxLength) 
        this.value = this.value.slice(0, this.maxLength);
    }

export default function OTP() {

  return (
    <>
    <div className="log-box">
           <h1>Welcome to Auction10</h1>
            <div className='form-container otp-container'>
                <form>
                <h2>Enter OTP: </h2>
                <span>Check your Email for a code</span>
                <div classname="otp-boxs">
                <input type="text" placeholder="0" name="digit-1" maxlength="1" onkeyup={Nextitem(this,1)} oninput={OTPitems} />
                <input type="text" placeholder="0" name="digit-2" maxlength="1" onkeyup={Nextitem(this,2)} oninput={OTPitems}   />
                <input type="text" placeholder="0" name="digit-3" maxlength="1" onkeyup={Nextitem(this,3)} oninput={OTPitems}  />
                <input type="text" placeholder="0" name="digit-4" maxlength="1" onkeyup={Nextitem(this,4)} oninput={OTPitems}  />
               </div> 
               <br/>
                <Link to="/passCreate"><button className='button otp-confirm'>Confirm</button></Link>
                </form>
            </div>
        </div>
    </>
  )
}
