import React, { Component } from 'react'
import {useState} from 'react'
import {Link } from "react-router-dom";
import "../Login/registration.css"

export default function registration(){
    const [email,setEmail] = useState('');
    const [password,setPassword] = useState('');
    
    const signIn = e =>{
        e.preventDefault();
        //sign up will be there
    }
    const register = e =>{
        e.preventDefault();
    }
    return (
        <>
        <div className="log-box">
           <h1>Welcome to Auction10</h1>
            <div className='log-container'>
                <h2>Sign In</h2>
                <form>
                    <h4>Email:</h4>
                    <input type='text' value={email} onChange={e=>setEmail(e.target.value)}></input>

                    <h4>Password:</h4>
                    <input type='password' value={password} onChange={e=>setPassword(e.target.value)}></input>

                    <button type='submit' onClick={signIn} className='button sign-in-button'>Sign In</button>
                </form>
                <p>By signing-in you agree to Auction10's Condition of use & Sale.</p>
                <p>_OR_</p>
                <Link to="/signup"><button onClick={register} className='button create-acc-button'>Create your Auction10 account</button></Link>
            </div>
        </div>
        </>
    )

}
